

function encode(buf, val, offset, size) {
  return buf.write(val, offset, size, 'utf8');
}

function decode(buf, offset, size) {
  return buf.toString('utf8', offset, offset + size);
};

function size(val) {
  return Buffer.byteLength(val, 'utf8');
}


exports.type   = 'string';
exports.size   = size;
exports.decode = decode;
exports.encode = encode;
